import React from 'react';
import '../../Assets/style/style.css';
import './style.css';
import { Container, Row, Col, Image } from 'react-bootstrap';
const About = () => {
    return (
        <section className="half-black-half-white " id="about">
            <h5 className="header">About</h5>
            <div class="header-bar"></div>
            <Container>
                <Row style={{ paddingTop: '50px' }}>
                    <Col>
                        <h6>Who's this guy?</h6>
                        <p>
                            I'm a Front-End Developer for ChowNow in Los
                            Angeles, CA. I have serious passion for UI effects,
                            animations and creating intuitive, dynamic user
                            experiences.
                        </p>
                    </Col>
                    <Col>
                        <Image
                            style={{ width: '350px', height: '450px' }}
                            src="https://i.ibb.co/qsfp4NH/IMG-20181227-161608-642.jpg"
                            rounded
                        />
                    </Col>
                </Row>
            </Container>
        </section>
    );
};

export default About;
