import React from 'react';
import { Nav, Navbar } from 'react-bootstrap';
import styled from 'styled-components';
import { Link, animateScroll as scroll } from 'react-scroll';

const Styles = styled.div`
    .navbar {
        background-color: white;
    }
    a,
    .navbar-nav .nav-link {
        color: black;
        text-decoration-color: red;

        &:hover {
            text-decoration-line: line-through;
            text-decoration-color: red;
            transition: $animate;
            position: relative;
            &:before,
            &:after {
                content: '';
                position: absolute;
                bottom: -10px;
                width: 0px;
                height: 5px;
                margin: 5px 0 0;
                transition: $animate;
                transition-duration: 0.75s;
                opacity: 0;
                background-color: darken(orange, 5%);
            }
        }
    }
`;

const NavigationBar = () => (
    <Styles>
        <nav className="nav" id="navbar">
            <div className="nav-content">
                <img
                    src={logo}
                    className="nav-logo"
                    alt="Logo"
                    onClick={this.scrollToTop}
                />
                <ul className="nav-items">
                    <li className="nav-item">
                        <Link
                            activeClass="active"
                            to="section1"
                            spy={true}
                            smooth={true}
                            offset={-70}
                            duration={500}
                        >
                            Section 1
                        </Link>
                    </li>
                    <li className="nav-item">
                        <Link
                            activeClass="active"
                            to="section2"
                            spy={true}
                            smooth={true}
                            offset={-70}
                            duration={500}
                        >
                            Section 2
                        </Link>
                    </li>
                    <li className="nav-item">
                        <Link
                            activeClass="active"
                            to="section3"
                            spy={true}
                            smooth={true}
                            offset={-70}
                            duration={500}
                        >
                            Section 3
                        </Link>
                    </li>
                    <li className="nav-item">
                        <Link
                            activeClass="active"
                            to="section4"
                            spy={true}
                            smooth={true}
                            offset={-70}
                            duration={500}
                        >
                            Section 4
                        </Link>
                    </li>
                    <li className="nav-item">
                        <Link
                            activeClass="active"
                            to="section5"
                            spy={true}
                            smooth={true}
                            offset={-70}
                            duration={500}
                        >
                            Section 5
                        </Link>
                    </li>
                </ul>
            </div>
        </nav>
    </Styles>
);
export default NavigationBar;
