import React from 'react';
import '../../Assets/style/style.css';
import './style.css';
import { Container, Row, Col, Image } from 'react-bootstrap';
import Typed from 'react-typed';
import 'react-typed/dist/animatedCursor.css';
import ScrollAnimation from 'react-animate-on-scroll';
import 'animate.css/animate.min.css';

const About = () => {
    return (
        <section className="half-black-half-white " id="about">
            <h5 className="header">About</h5>
            <div class="header-bar"></div>
            <Container>
                <Row
                    style={{
                        paddingTop: '50px',
                        fontSize: '22px',
                        color: '#ababab',
                    }}
                >
                    {' '}
                    <ScrollAnimation animateIn="fadeIn">
                        <Col style={{ paddingTop: '100px' }}>
                            duration="2" animateIn="fadeInLeft " Who's this guy?
                            <p>
                                I'm a Front-End Developer Living In Istanbul,
                                Turkey.
                                <br></br>I have serious passion for UI effects
                                and animations.I love to Create{' '}
                                <Typed
                                    className="strongy"
                                    strings={['Fast', 'Responsive', 'Dynamic']}
                                    typeSpeed={50}
                                    backSpeed={50}
                                    loop
                                />{' '}
                                <br></br> Websites With Great User experiences.
                            </p>
                        </Col>
                    </ScrollAnimation>
                    <Col>
                        <Image
                            style={{ width: '350px', height: '450px' }}
                            src="https://i.ibb.co/qsfp4NH/IMG-20181227-161608-642.jpg"
                            rounded
                        />
                    </Col>
                </Row>
            </Container>
        </section>
    );
};

export default About;
